package loops;

public class ForEachLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {1,2,3,4,5,6,7};
		
		for(int i:a) {
			System.out.println(i);
		}
		
		//string array...
		String s[]= {"mahesh","sailaja","srilatha","selenium","hyderabad"};
		for(String x:s) {
			System.out.println(x);
		}
		
	}

}
